// Commit 1: Adicionando estado inicial para o mapa e caixas de pesquisa
import React, { useState, useCallback, useMemo } from "react";
import {
  GoogleMap,
  Marker,
  LoadScript,
  StandaloneSearchBox,
  DirectionsService,
  DirectionsRenderer,
} from "@react-google-maps/api";
import {
  Card,
  CardContent,
  CardFooter,
  CardTitle,
} from "../components/ui/card";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";

export interface MapsProps {}

const Maps = () => {
  // Estado para o mapa, caixas de pesquisa, e locais de embarque e desembarque
  const [map, setMap] = useState<google.maps.Map>();
  const [searchBoxA, setSearchBoxA] = useState<google.maps.places.SearchBox>();
  const [searchBoxB, setSearchBoxB] = useState<google.maps.places.SearchBox>();
  const [targetA, settargetA] = useState<google.maps.LatLngLiteral>();
  const [targetB, settargetB] = useState<google.maps.LatLngLiteral>();

  const [origin, setOrigin] = useState<google.maps.LatLngLiteral | null>(null);
  const [destination, setDestination] =
    useState<google.maps.LatLngLiteral | null>(null);

  const [response, setResponse] =
    useState<google.maps.DistanceMatrixResponse | null>(null);

  //Posição inicial do mapa
  const position = {
    lat: -23.525505,
    lng: -46.667358,
  };

  // Função de carregamento do mapa
  const onMapLoad = (map: google.maps.Map) => {
    setMap(map);
  };

  // Funções de carregamento das caixas de pesquisa A e B
  const onLoadA = (ref: google.maps.places.SearchBox) => {
    setSearchBoxA(ref);
  };

  const onLoadB = (ref: google.maps.places.SearchBox) => {
    setSearchBoxB(ref);
  };

  // Função para lidar com alterações no local da pesquisa A
  const onPlacesChangedA = () => {
    const places = searchBoxA!.getPlaces();
    console.log(places);
    const place = places![0];
    const location = {
      lat: place?.geometry?.location?.lat() || 0,
      lng: place?.geometry?.location?.lng() || 0,
    };
    settargetA(location);
    setOrigin(null);
    setDestination(null);
    setResponse(null);
    map?.panTo(location);
  };

  // Função para lidar com alterações no local da pesquisa B
  const onPlacesChangedB = () => {
    const places = searchBoxB!.getPlaces();
    console.log(places);
    const place = places![0];
    const location = {
      lat: place?.geometry?.location?.lat() || 0,
      lng: place?.geometry?.location?.lng() || 0,
    };
    settargetB(location);
    setOrigin(null);
    setDestination(null);
    setResponse(null);
    map?.panTo(location);
  };

  // Função para traçar a rota entre os dois locais
  const traceRoute = () => {
    if (targetA && targetB) {
      setOrigin(targetA);
      setDestination(targetB);
    }
  };

  // Commit 2: Definindo a requisição de direções com base nos pontos de origem e destino
  const directionsServiceOptions =
    // @ts-ignore
    useMemo<google.maps.DirectionsRequest>(() => {
      return {
        origin,
        destination,
        travelMode: "DRIVING",
      };
    }, [origin, destination]);

  // Commit 3: Callback para lidar com a resposta do serviço de direções
  const directionsCallback = useCallback((res: any) => {
    if (res !== null && res.status === "OK") {
      setResponse(res);
    } else {
      console.log(res);
    }
  }, []);

  // Commit 4: Configurando o renderer de direções
  const directionsRendererOptions = useMemo<any>(() => {
    return {
      directions: response,
    };
  }, [response]);

  // Commit 5: Renderizando a interface com o mapa e os controles
  return (
    <div className="w-full h-full">
      <LoadScript
        googleMapsApiKey={"AIzaSyDmyOn9q7XLenV8V4onkqT2tXlpYuadgjw"}
        libraries={["places"]}
      >
        <div className="grid md:grid-cols-2 h-64">
          <div className="flex items-center justify-center p-4">
            <Card className="p-4">
              <CardTitle className="my-4 text-6xl title">
                Solicite agora mesmo sua viagem
              </CardTitle>
              <CardContent>
                <div className="grid w-full items-center gap-4">
                  {/* Caixas de pesquisa para origem e destino */}
                  <StandaloneSearchBox
                    onLoad={onLoadA}
                    onPlacesChanged={onPlacesChangedA}
                  >
                    <Input placeholder="Local de embarque" />
                  </StandaloneSearchBox>
                  <StandaloneSearchBox
                    onLoad={onLoadB}
                    onPlacesChanged={onPlacesChangedB}
                  >
                    <Input placeholder="Local de desembarque" />
                  </StandaloneSearchBox>
                </div>
                <CardFooter className="justify-end mt-4">
                  <Button onClick={traceRoute}>Traçar rota</Button>
                </CardFooter>
              </CardContent>
            </Card>
          </div>
          <div className="flex align-center">
            <div className="aspect-square flex justify-center items-center">
              <GoogleMap
                onLoad={onMapLoad}
                options={{ streetViewControl: false }}
                mapContainerStyle={{ width: "70%", height: "70%" }}
                center={position}
                zoom={15}
              >
                {/* Marcadores para os pontos de origem e destino */}
                {!response && targetA && <Marker position={targetA} />}
                {!response && targetB && <Marker position={targetB} />}

                {/* Serviço de direções */}
                {origin && destination && (
                  <DirectionsService
                    options={directionsServiceOptions}
                    callback={directionsCallback}
                  />
                )}

                {/* Renderizando a rota se a resposta existir */}
                {response && directionsRendererOptions && (
                  <DirectionsRenderer options={directionsRendererOptions} />
                )}
              </GoogleMap>
            </div>
          </div>
        </div>
      </LoadScript>
    </div>
  );
};

export default Maps;
